    private void zapiszButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_zapiszButtonActionPerformed
        //sprawdzenie czy wype≈Çnione

        if (czasTextField.getText().equals("") || nazwaTextField.getText().equals("") || graTextField.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Niepe≈Çne dane!", "B≈ÇƒÖd", JOptionPane.ERROR_MESSAGE);
        } else {
            //
            new NowyTurniejThread(nazwaTextField.getText(), graTextField.getText(), jComboBox1.getSelectedItem().toString(), czasTextField.getText()).actionPerformed(null);
            //

            var powrotDoListy = new ListaTurniejowView(user);
            powrotDoListy.setLocation(this.getLocation().x, this.getLocation().y);
            powrotDoListy.setVisible(true);

            this.dispose();
        }
    }//GEN-LAST:event_zapiszButtonActionPerformed

